INSERT INTO gl_article_system VALUES ('14', '加盟政策', '&lt;div&gt;&lt;img src=&quot;images/pt17.gif&quot; width=&quot;325&quot; height=&quot;29&quot;&gt;&lt;br&gt;\r\n      &lt;br&gt;\r\n  &lt;/div&gt;&lt;div&gt;\r\n    &lt;div style=&quot;background-color:#FFF6EB; color:#B55900; margin:10px 0 5px 0; padding-left:15px; line-height:32px;&quot;&gt;加盟条件：&lt;/div&gt;\r\n	1、具备独立法人资格，符合国家针对快递行业的政策、法规要求；&lt;br&gt;\r\n2、取得《快递业务经营许可证》资质；&lt;br&gt;\r\n3、有固定的软硬件配套设施；&lt;br&gt;\r\n4、根据所加盟区域大小配备相应的服务车辆和业务人员；&lt;br&gt;\r\n5、遵守国梁物流网络管理制度，服从国梁物流网络管理中心统一管理；&lt;/div&gt;&lt;br&gt;\r\n&lt;div&gt;\r\n    &lt;div style=&quot;background-color:#FFF6EB; color:#B55900; margin:10px 0 5px 0; padding-left:15px; line-height:32px;&quot;&gt;加盟相关要件：&lt;/div&gt;\r\n	1、加盟者身份证复印件&lt;br&gt;\r\n2、个人履历表及当地相关部门有效签字证明（附有效公章）&lt;br&gt;\r\n3、营业执照复印件&lt;br&gt;\r\n4、所加盟区域的派送范围（附当地地图）&lt;/div&gt;', '', '1337101890', '1');
